package tom;

public class StringReverse {
	public static void main(String args[]){
		
		String str="raju",rver="";
		char ch;
		System.out.println("Original word:"+ str);
		for(int i=0;i<str.length();i++){
			ch=str.charAt(i);
			rver=ch+rver;
		}
		System.out.println("reverse word:"+ rver);
		
		                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
	}

}
